import React, { useEffect, useState } from "react";
import CountryCard from "../CountryCard";

export default function ByRegion() {
  const [region, setRegion] = useState("Africa");
  const [countries, setCountries] = useState([]);

  const handleChange = (e) => {
    e.target.checked && setRegion(e.target.value);
  };

  useEffect(() => {
    fetch(`https://restcountries.com/v3.1/region/${region}`)
      .then((res) => res.json())
      .then((data) => setCountries(data));
  }, [region]);

  return (
    <div>
      {console.log(countries)}
      <input
        type={"radio"}
        name="region"
        defaultChecked
        value="Africa"
        onChange={handleChange}
      />
      <label style={{ marginRight: "15px" }}>Africa</label>
      <input
        type={"radio"}
        name="region"
        value="America"
        onChange={handleChange}
      />
      <label style={{ marginRight: "15px" }}>America</label>
      <input
        type={"radio"}
        name="region"
        value="Asia"
        onChange={handleChange}
      />
      <label style={{ marginRight: "15px" }}>Asia</label>
      <input
        type={"radio"}
        name="region"
        value="Europe"
        onChange={handleChange}
      />
      <label style={{ marginRight: "15px" }}>Europe</label>
      <input
        type={"radio"}
        name="region"
        value="Oceania"
        onChange={handleChange}
      />
      <label>Oceania</label>

      <div className="cardsContainer">
        {countries &&
          countries.map((x, i) => <CountryCard country={x} key={i} />)}
      </div>
    </div>
  );
}
